<span class="line">    <span class="token property">background</span><span class="token punctuation">:</span> <span class="token url"><span class="token function">url</span><span class="token punctuation">(</span><span class="token string url">&quot;../images/ad.png&quot;</span><span class="token punctuation">)</span></span> no-repeat center top<span class="token punctuation">;</span></span>
